<?phprequire_once('includes/config.php');if ($_SERVER['REQUEST_METHOD'] === 'POST') {    $entreprise = $_POST['entreprise'];    $poste = $_POST['poste'];    $departement = $_POST['departement'];    $salaire = $_POST['salaire'];    $date = $_POST['date_candidature'];    $statut = $_POST['statut'];    $plateforme = $_POST['plateforme'];    $lien = $_POST['lien_offre'];    $commentaire = $_POST['commentaire'];    $contact = $_POST['contact'];$stmt = $pdo->prepare("INSERT INTO candidatures (entreprise, poste, departement, salaire, date_candidature, statut, plateforme, lien_offre, commentaire, contact)VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)");$stmt->execute([$entreprise, $poste, $departement, $salaire, $date, $statut, $plateforme, $lien, $commentaire, $contact]);    header("Location: index.php");    exit;}?><!DOCTYPE html><html lang="fr"><head>  <meta charset="UTF-8">  <title>Ajouter une candidature</title>  <link rel="stylesheet" href="../style/style.css">  <link rel="stylesheet" href="/style/emploi.css"></head><body>  <header class="emploi-header">    <h1>➕ Ajouter une candidature</h1>  </header><form method="post" action="">  <div class="form-grid">    <div class="form-group">      <label for="entreprise">Entreprise</label>      <input type="text" id="entreprise" name="entreprise" required>    </div>    <div class="form-group">      <label for="poste">Poste</label>      <input type="text" id="poste" name="poste" required>    </div>    <div class="form-group">      <label for="departement">Département</label>      <input type="number" id="departement" name="departement" required>    </div>    <div class="form-group">      <label for="salaire">Salaire</label>      <input type="text" id="salaire" name="salaire">    </div>    <div class="form-group">      <label for="date_candidature">Date</label>      <input type="date" id="date_candidature" name="date_candidature" required>    </div>    <div class="form-group">      <label for="statut">Statut</label>      <select id="statut" name="statut">        <option value="En attente">⏳ En attente</option>        <option value="Entretien">📞 Entretien</option>        <option value="Refusé">❌ Refusé</option>        <option value="Accepté">✅ Accepté</option>      </select>    </div>    <div class="form-group">      <label for="plateforme">Plateforme</label>      <input type="text" id="plateforme" name="plateforme">    </div>    <div class="form-group">      <label for="lien_offre">Lien de l'offre</label>      <input type="url" id="lien_offre" name="lien_offre">    </div>    <div class="form-group full">      <label for="commentaire">Commentaire</label>      <textarea id="commentaire" name="commentaire"></textarea>    </div>    <div class="form-group full">      <label for="contact">Contact (qualité, mail ou tel)</label>      <textarea id="contact" name="contact"></textarea>    </div>  </div>  <div class="form-buttons">    <button type="submit" class="btn btn-add">💾 Ajouter</button>    <a href="index.php" class="btn btn-delete">↩️ Retour</a>  </div></form></body></html>