<?phprequire_once('includes/config.php');if (!isset($_GET['id'])) {    die("ID manquant.");}$id = (int)$_GET['id'];// Récupération des données de la candidature$stmt = $pdo->prepare("SELECT * FROM candidatures WHERE id = ?");$stmt->execute([$id]);$c = $stmt->fetch(PDO::FETCH_ASSOC);if (!$c) {    die("Candidature introuvable.");}// Si le formulaire est soumisif ($_SERVER['REQUEST_METHOD'] === 'POST') {    $entreprise = $_POST['entreprise'];    $poste = $_POST['poste'];    $departement = $_POST['departement'];    $salaire = $_POST['salaire'];    $date = $_POST['date_candidature'];    $statut = $_POST['statut'];    $plateforme = $_POST['plateforme'];    $lien = $_POST['lien_offre'];    $commentaire = $_POST['commentaire'];    $contact = $_POST['contact'];    $update = $pdo->prepare("UPDATE candidatures SET entreprise = ?, poste = ?, departement = ?, salaire = ?, date_candidature = ?, statut = ?, plateforme = ?, lien_offre = ?, commentaire = ?, contact = ? WHERE id = ?");    $update->execute([$entreprise, $poste, $departement, $salaire, $date, $statut, $plateforme, $lien, $commentaire, $contact, $id]);    header("Location: index.php");    exit;}?><!DOCTYPE html><html lang="fr"><head>  <meta charset="UTF-8">  <title>Modifier la candidature</title>  <link rel="stylesheet" href="../style/style.css">  <link rel="stylesheet" href="/style/emploi.css"></head><body>  <header class="emploi-header">    <h1>✏️ Modifier la candidature</h1>  </header><form method="post" action="">  <div class="form-grid">    <div class="form-group">      <label for="entreprise">Entreprise</label>      <input type="text" id="entreprise" name="entreprise" value="<?= htmlspecialchars($c['entreprise']) ?>" required>    </div>    <div class="form-group">      <label for="poste">Poste</label>      <input type="text" id="poste" name="poste" value="<?= htmlspecialchars($c['poste']) ?>" required>    </div>    <div class="form-group">      <label for="departement">Département</label>      <input type="number" id="departement" name="departement" value="<?= htmlspecialchars($c['departement']) ?>" required>    </div>    <div class="form-group">      <label for="salaire">Salaire</label>      <input type="text" id="salaire" name="salaire" value="<?= htmlspecialchars($c['salaire']) ?>">    </div>    <div class="form-group">      <label for="date_candidature">Date</label>      <input type="date" id="date_candidature" name="date_candidature" value="<?= htmlspecialchars($c['date_candidature']) ?>" required>    </div>    <div class="form-group">      <label for="statut">Statut</label>      <select id="statut" name="statut">        <option value="En attente" <?= $c['statut'] === 'En attente' ? 'selected' : '' ?>>⏳ En attente</option>        <option value="Entretien" <?= $c['statut'] === 'Entretien' ? 'selected' : '' ?>>📞 Entretien</option>        <option value="Refusé" <?= $c['statut'] === 'Refusé' ? 'selected' : '' ?>>❌ Refusé</option>        <option value="Accepté" <?= $c['statut'] === 'Accepté' ? 'selected' : '' ?>>✅ Accepté</option>      </select>    </div>    <div class="form-group">      <label for="plateforme">Plateforme</label>      <input type="text" id="plateforme" name="plateforme" value="<?= htmlspecialchars($c['plateforme']) ?>">    </div>    <div class="form-group">      <label for="lien_offre">Lien de l'offre</label>      <input type="url" id="lien_offre" name="lien_offre" value="<?= htmlspecialchars($c['lien_offre']) ?>">    </div>    <div class="form-group full">      <label for="commentaire">Commentaire</label>      <textarea id="commentaire" name="commentaire"><?= htmlspecialchars($c['commentaire']) ?></textarea>    </div>    <div class="form-group full">      <label for="contact">Contact (qualité, mail ou tel)</label>      <textarea id="contact" name="contact"><?= htmlspecialchars($c['contact']) ?></textarea>    </div>  </div>  <div class="form-buttons">    <button type="submit" class="btn btn-edit">💾 Enregistrer les modifications</button>    <a href="index.php" class="btn btn-delete">↩️ Retour</a>  </div></form></body></html>